vec4 Effect2(vec2 uv) {
    // Create a hexagonal grid pattern with animation
    vec2 st = uv * 10.0;
    vec2 iSt = floor(st);
    vec2 fSt = fract(st);

    // Add some noise and movement to the grid points
    float time = iTime * 0.5;
    vec3 color = hsl2rgb(vec3(0.5 + 0.5*sin(time + iSt.x*0.1), 0.7, 0.8));

    // Create a flowing network pattern
    vec2 offset = vec2(
        sin(time + iSt.y * 0.1) * 0.3,
        cos(time + iSt.x * 0.1) * 0.3
    );

    // Calculate distance from the current point to the grid points
    vec2 d = fSt - 0.5;
    float dist = length(d);

    // Add some turbulence to create organic movement
    float turb = turbulent(vec3(st + time, 0.0)) * 0.1;
    dist += turb;

    // Create a pulsing effect
    float pulse = sin(time) * 0.5 + 0.5;
    float stroke = smoothstep(0.4 - pulse*0.2, 0.4 + pulse*0.2, dist);

    // Add some depth perception
    vec3 finalColor = color * (1.0 - abs(pulse - 0.5)*0.5);
    finalColor *= 1.0 - length(offset) * 0.5;

    return vec4(finalColor, 1.0);
}
