vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float angle = 2.0 * pi * iTime / 10.0;
    vec2 rotatedUv = vec2(
        cos(angle)*uv.x - sin(angle)*uv.y,
        sin(angle)*uv.x + cos(angle)*uv.y
    );

    // Create sine wave pattern with time-based frequency
    float freq = 5.0 + sin(iTime / 3.0) * 2.0;
    vec2 st = rotatedUv * freq;

    // Add turbulence to create dynamic variations
    float noiseVal = noise(st + iTime);
    noiseVal = turbulent(vec3(st, iTime)) * 0.5 + 0.5;

    // Create RGB shifts with time-based phase differences
    vec3 color = vec3(
        sin(st.x * 2.0 + iTime) * 0.5 + 0.5,
        cos(st.y * 2.0 - iTime/2.0) * 0.5 + 0.5,
        sin((st.x + st.y)*1.5 + iTime*0.8) * 0.5 + 0.5
    );

    // Mix noise with color and add brightness variation
    vec3 finalColor = mix(color, vec3(noiseVal), 0.4);
    finalColor *= sin(iTime / 2.0) * 0.5 + 0.7;

    return vec4(finalColor, 1.0);
}
