vec4 Effect2(vec2 uv) {
    // Base color transition using HSL
    vec3 baseColor = hsl2rgb(vec3(0.5 * sin(iTime * 0.5) + 0.5, 0.7, 0.6));

    // Create flowing liquid effect using noise and turbulence
    float flow1 = turbulent(uv * 4.0 + vec2(iTime)) * 0.5;
    float flow2 = turbulent(uv * 8.0 + vec2(iTime * 0.7)) * 0.3;
    float flow3 = turbulent(uv * 16.0 + vec2(iTime * 0.4)) * 0.2;

    // Combine flows with different scales and speeds
    float liquidFlow = (flow1 + flow2 + flow3) * 0.5 + 0.5;

    // Create color transitions based on UV coordinates and time
    vec3 finalColor = mix(
        baseColor,
        hsl2rgb(vec3(0.3 * sin(iTime * 0.3) + 0.6, 0.8, 0.7)),
        liquidFlow
    );

    // Add dynamic movement to the color transitions
    finalColor *= 1.0 + 0.5 * sin(iTime + uv.x * 2.0);
    finalColor = clamp(finalColor, 0.0, 1.0); // Ensure colors stay within valid range

    return vec4(finalColor, 1.0);
}
