vec4 Effect2(vec2 uv) {
    // Create flowing sine wave pattern with time offset
    float time = iTime * 0.5;

    // Base sine wave pattern
    vec2 st = uv * 10.0 + vec2(time, 0.0);
    float wave1 = sin(st.x) * 0.5;

    // Add noise and turbulence for more organic feel
    vec3 noisePos = vec3(uv * 4.0, time);
    float noiseVal = noise(noisePos) * 0.2;
    float turb = turbulent(vec2(uv * 16.0)) * 0.1;

    // Combine patterns and add metallic color shifts
    float pattern = wave1 + sin(wave1 * 4.0 + time) * 0.3 + noiseVal + turb;

    // Create metallic color using HSL
    vec3 colorHSL = vec3(0.6, 0.8, 0.5 + abs(pattern) * 0.2);
    vec3 rgbColor = hsl2rgb(colorHSL);

    // Add highlight/specular effect
    vec2 lightDir = vec2(sin(time), cos(time)) * 0.5;
    float light = max(0.0, dot(vec2(dFdx(pattern), dFdy(pattern)), lightDir));

    // Final color with metallic sheen
    rgbColor *= (1.0 + light * 0.6);

    return vec4(rgbColor, 1.0);
}
