vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Polar coordinates
    vec2 pos = uv - 0.5;
    float r = length(pos);
    float theta = atan(pos.y, pos.x);

    // Nebula color and pulsation
    vec3 nebColor = hsl(theta * 0.1 + time * 0.5, 0.6, 0.5);
    float pulse = sin(time * 2.0 + r * 4.0) * 0.5 + 0.5;
    nebColor *= (pulse + turbulent(vec3(pos.x, pos.y, time)) * 0.2);

    // Star particles
    float star = noise(vec3(uv * 100.0, time));
    star = step(0.95, star) * 0.8;
    nebColor += star * hsl(time * 0.5 + theta * 0.2, 1.0, 1.0);

    // Add glow around stars
    float glow = noise(vec3(uv * 50.0, time)) * 0.5;
    nebColor *= (glow + 0.5) * turbulent(vec3(pos.x, pos.y, time)) * 0.5;

    return vec4(nebColor, 1.0);
}
