vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Create polar coordinates
    float radius = length(st);
    float theta = atan2(st.y, st.x);

    // Create hexagonal pattern by multiplying angle by 6
    theta *= 6.0;

    // Add time-based rotation
    theta += iTime * 4.0;

    // Use sine of multiplied angle for pulsating effect
    float pulse = sin(theta + radius * 10.0) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        radius * 360.0,  // Hue varies with distance from center
        0.7,             // Saturation
        pulse           // Lightness varies with pulsing effect
    );

    // Convert to RGB and mix with white based on noise
    vec3 color = hsl2rgb(hslColor) * (1.0 - turbulent(st * 4.0)) + vec3(1.0) * turbulent(st * 4.0);

    return vec4(color, 1.0);
}
