vec4 Effect2(vec2 uv) {
    // Base frequency for waves
    float freq = 4.0;

    // Create two sine wave patterns moving in opposite directions
    float wave1 = sin(uv.x * freq + iTime) * 0.5 + 0.5;
    float wave2 = sin(-uv.y * freq - iTime) * 0.5 + 0.5;

    // Combine waves with some offset and scale
    vec2 waves = vec2(wave1, wave2);

    // Create a color based on HSL values that shift over time
    vec3 baseColor = hsl(
        mod(iTime * 0.2 + uv.x * 2.0 + uv.y * 3.0, 1.0),  // Hue
        0.7,  // Saturation
        0.5   // Lightness
    );

    // Mix colors based on wave patterns
    vec3 finalColor = mix(
        baseColor,
        hsl(
            mod(iTime * 0.2 + uv.x * 3.0 + uv.y * 2.0, 1.0),  // Hue
            0.7,
            0.5
        ),
        waves.x * waves.y * 0.8 + 0.2
    );

    // Add some noise to create dynamic variations
    vec2 noiseUV = uv * 20.0 + iTime;
    float noiseVal = turbulent(noiseUV) * 0.3;
    finalColor *= 1.0 + noiseVal;

    return vec4(finalColor, 1.0);
}
