vec4 Effect2(vec2 uv) {
    // Scale factor for grid size
    float scale = 10.0;

    // Create a grid pattern using floor and fract functions
    vec2 gridPos = floor(uv * scale);
    vec2 cellPos = fract(uv * scale);

    // Calculate triangle position within each grid cell
    bool triangleA = (cellPos.x + cellPos.y) < 1.0;

    // Create animated color transitions using HSL colors
    vec3 hslColor = vec3(
        mod(gridPos.x + gridPos.y * 0.5 + iTime * 0.2, 1.0),  // Animated hue
        0.7,  // Saturation
        0.6   // Lightness
    );

    // Convert HSL to RGB and add depth effect using noise
    vec3 color = hsl2rgb(hslColor);
    float depth = turbulent(vec3(cellPos * 10.0, iTime * 0.5)) * 0.5 + 0.5;

    // Mix colors based on triangle position and depth
    if (triangleA) {
        color *= mix(1.0, 0.8, depth);
    } else {
        color *= mix(0.8, 1.0, depth);
    }

    return vec4(color, 0.9);
}
