vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv - 0.5;
    float theta = atan(st.y, st.x);
    float r = length(st);

    // Add time-based rotation and noise
    theta += iTime * 0.1 + turbulent(vec3(theta, r, iTime)) * 0.2;

    // Create color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        (theta / PI + 0.5) * 0.6 + iTime * 0.1,
        0.7 + turbulent(vec2(r, iTime)) * 0.3,
        0.5 + turbulent(vec2(theta, r)) * 0.2
    ));

    // Add perspective distortion
    float depth = 1.0 / (r + 1.0);
    color *= depth;

    return vec4(color, 1.0);
}
