vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for circular motion
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st) * 0.5 + 0.5;

    // Create opening/closing effect using time
    float mouthOpen = sin(iTime * 3.0) * 0.5 + 0.5;
    float scaledRadius = radius * (1.0 - mouthOpen * 0.5);

    // Add noise for organic movement
    vec2 noiseUV = uv * 4.0 + iTime;
    float noiseValue = turbulent(noiseUV) * 0.1;

    // Create color based on position and time
    vec3 color = hsl(
        (angle / PI + 1.0) * 0.5 + iTime * 0.2,
        0.7 + noiseValue * 0.3,
        0.6 + scaledRadius * 0.4
    );

    // Add inner details
    float innerDetail = smoothstep(0.8, 1.0, radius);
    color += hsl(
        (angle / PI + 1.0) * 0.5 + iTime * 0.3,
        0.6,
        0.4
    ) * innerDetail;

    return vec4(color, 1.0);
}
