vec4 Effect2(vec2 uv) {
    // Create a base color using HSL with position and time
    vec3 baseColor = hsl2rgb(vec3(uv.x * 5.0 + iTime, 0.5, 0.5));

    // Add dynamic noise-based variation
    float noise1 = noise(uv * 4.0 + vec2(iTime));
    float noise2 = noise((uv + vec2(iTime)) * 8.0);
    float turbulence = (noise1 + noise2) * 0.5;

    // Combine colors with turbulence
    vec3 finalColor = mix(baseColor, hsl2rgb(vec3(uv.y * 5.0 + iTime, 0.5, 0.5)), abs(turbulence));

    return vec4(finalColor, 1.0);
}
