vec4 Effect2(vec2 uv) {
    // Create rotating color pattern using time and position
    float hue = 0.5 + 0.5 * sin(iTime * 2.0 + uv.x * PI);

    // Add noise-based turbulence for dynamic patterns
    vec3 baseColor = hsl2rgb(vec3(hue, 1.0, 0.5));
    float turb = turbulent(uv * 10.0 + iTime);

    // Combine color and turbulence with layered noise
    vec3 finalColor = mix(baseColor, hsl2rgb(vec3(hue + 0.3, 1.0, 0.5)), turb * 0.5 + 0.5);
    finalColor += hsv2rgb(vec3(0.0, 0.0, turbulent(uv * 20.0 + iTime) * 0.5 + 0.5));

    return vec4(finalColor * 1.5, 1.0);
}
