vec4 Effect2(vec2 uv) {
    // Scale and translate UV coordinates
    vec2 st = uv * 10.0 - 5.0;

    // Convert to polar coordinates
    float angle = atan2(st.y, st.x) + iTime;
    float radius = length(st);

    // Create hexagonal pattern
    float hex = 0.0;
    for(float a = 0.0; a < PI*2.0; a += PI/3.0) {
        vec2 p = vec2(cos(a + angle), sin(a + angle)) * radius;
        hex += abs(sin(p.x * 0.5 + iTime) + cos(p.y * 0.5 + iTime));
    }

    // Create pulsating effect
    float pulse = sin(iTime * 0.5 + radius * 0.2) * 0.5 + 0.5;

    // Calculate color based on hexagonal pattern and time
    vec3 color = hsl2rgb(vec3(
        fract(radius * 0.1 + iTime),
        0.7,
        pulse * 0.8 + 0.2
    ));

    // Add mouse interaction
    if(length(uv - 0.5) < 0.1) {
        color = hsl2rgb(vec3(
            fract(iTime * 0.2),
            1.0,
            0.5
        ));
    }

    return vec4(color, 1.0);
}
