vec4 Effect2(vec2 uv) {
    // Scale and offset coordinates for better pattern distribution
    vec2 st = uv * 8.0 - 4.0;

    // Add time-based movement to the coordinates
    st.x += sin(iTime * 0.5 + st.y) * 0.5;
    st.y += cos(iTime * 0.6 + st.x) * 0.5;

    // Create a color based on position and time
    vec3 color = hsl2rgb(vec3(
        (st.x + iTime) * 0.1,
        sin(length(st) * 0.5 + iTime) * 0.5 + 0.5,
        cos(iTime * 0.4) * 0.5 + 0.5
    ));

    // Add noise for texture and movement
    float noiseValue = noise(vec2(
        st.x * 0.5 + iTime * 0.3,
        st.y * 0.5 + iTime * 0.4
    )) * 0.5 + 0.5;

    // Combine color with noise for final effect
    vec3 finalColor = mix(
        color,
        hsl2rgb(vec3((iTime) * 0.1, 0.8, 0.6)),
        noiseValue * 0.4
    );

    return vec4(finalColor, 1.0);
}
