vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern
    vec2 st = uv * 5.0 + iTime;

    // Add rotation for flowing effect
    float angle = sin(iTime * 0.5) * 0.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    st = rot * st;
    // Generate noise layers with different frequencies
    float n1 = noise(st);
    float n2 = noise(st * 2.0 + vec2(1.0, 1.0)) * 0.5;
    float n3 = turbulent(st * 4.0) * 0.25;
    // Combine noise layers
    float liquid = (n1 + n2 + n3) * 0.5 + 0.5;
    // Create color transitions using HSL
    vec3 colorBase = hsl2rgb(vec3(liquid * 360.0, 0.7, 0.5));

    // Add dynamic shadows and highlights
    float light = sin(iTime) * 0.5 + 0.5;
    vec3 finalColor = mix(colorBase * (1.0 - light), colorBase * 1.5, liquid);
    return vec4(finalColor, 1.0);
}
