vec4 Effect2(vec2 uv) {
    // Create a shifting pattern using time
    vec2 st = uv + sin(uv * 10.0 + iTime) / 20.0;

    // Add turbulence to create flowing effect
    float t = turbulent(st);

    // Create color variations using HSL
    vec3 c1 = hsl2rgb(vec3(t * 0.5 + 0.5, 0.8, 0.8));
    vec3 c2 = hsl2rgb(vec3(turbulent(st * 2.0) * 0.5 + 0.5, 0.6, 0.7));

    // Combine colors with noise
    float mixVal = noise(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;
    vec3 finalColor = mix(c1, c2, mixVal);

    // Add some dynamic movement using sine waves
    finalColor *= sin(iTime) * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
