vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv - 0.5;
    float radius = length(st);
    float theta = atan2(st.y, st.x);

    // Create rotating sine wave pattern
    float time = iTime * 0.1;
    float wave = sin(theta + time) * 4.0 + sin(3.0*(theta - time)) * 0.5;

    // Create color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        radius * 0.6 + time,   // Hue based on radius and time
        0.7,                  // Saturation
        0.5                   // Lightness
    ));

    // Add noise for texture
    float noise = turbulent(uv * 4.0) * 0.1;
    color += vec3(noise);

    // Mix with black background
    color *= sin(wave + time * 2.0) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
