vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 coord = uv * 2.0 - 1.0;
    float r = length(coord);
    float theta = atan2(coord.y, coord.x);
    // Create rotating color wheel effect
    const float numSpokes = 6.0;
    const float rotationSpeed = 0.5;

    theta += iTime * rotationSpeed;

    // Calculate spoke position and create smooth transitions between colors
    float spoke = mod(theta * numSpokes, 1.0);
    spoke = smoothstep(0.0, 1.0, spoke);

    // Create color transition from red to green to blue
    vec3 color = vec3(
        smoothstep(0.5, 1.0, spoke),
        smoothstep(0.0, 1.0, spoke) * (1.0 - smoothstep(0.5, 1.0, spoke)),
        smoothstep(0.0, 0.5, spoke)
    );

    // Add noise to create dynamic radius effect
    float noiseValue = noise(vec2(iTime * 0.5, r));
    color *= mix(r, abs(sin(iTime)) + 0.5, noiseValue);

    return vec4(color, 1.0);
}
