vec4 Effect2(vec2 uv) {
    // Create a base color using HSL
    vec3 c = hsl(0.5 + 0.2*sin(iTime*0.5 + uv.x * 10.), 
                 0.6, 
                 0.8);

    // Add noise to the color components
    c += vec3(noise(uv * 400.0 + iTime), 
              noise(uv * 400.0 + iTime + 123.456),
              noise(uv * 400.0 + iTime + 678.9));

    // Create a turbulent pattern
    float t = turbulent(uv * 5.0 + vec2(sin(iTime), cos(iTime)));

    // Mix colors based on turbulence
    c = mix(c, 
            hsl(0.1 + t*0.3, 
                0.7, 
                0.6),
            max(t*2.0 - 0.5, 0.0));

    return vec4(c, 1.0);
}
