vec4 Effect2(vec2 uv) {
    // Create a pulsating effect using time
    float time = iTime * 0.5;

    // Add mouse movement influence (assuming mouse coordinates are normalized)
    vec2 st = uv * 10.0 + time;

    // Use noise to create a dynamic pattern
    float n = noise(st);

    // Create geometric patterns using polar coordinates
    vec2 polar = vec2(length(uv), atan2(uv.y, uv.x));

    // Add turbulence for more complex movement
    float turb = turbulent(polar * 0.5 + time);

    // Combine noise and turbulence with color transformations
    vec3 color = hsl2rgb(vec3(
        (n + turb) * 0.5 + 0.5,   // Hue
        1.0 - abs(sin(time)),     // Saturation
        0.5                       // Lightness
    ));

    return vec4(color, 1.0);
}
