vec4 Effect2(vec2 uv) {
    // Scale and rotate coordinates over time
    vec2 st = uv * 5.0;
    float time = iTime * 0.5;

    // Create a rotation matrix
    mat2 rot = mat2(cos(time), -sin(time), sin(time), cos(time));
    st = st * rot;

    // Compute noise layers with different scales
    float n1 = noise(st * 0.5);
    float n2 = noise(st + vec2(123.456, 789.123)) * 0.5;
    float n3 = noise(vec3(st * 2.0, time)) * 0.25;

    // Combine noise values with time-based variation
    float value = (n1 + n2 + n3) * 0.5 + 0.5;
    value += sin(time * 2.0) * 0.2;

    // Create color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        fract(value * 4.0 + time),  // Hue variation
        0.7,                       // Saturation
        0.5 + value * 0.5          // Value modulation
    ));

    return vec4(color, 1.0);
}
