vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);

    // Add rotation over time
    theta += iTime * 0.5f; // Rotate at 0.5 radians per second

    // Create color transitions using HSL colors
    vec3 color1 = hsl(0.0, 0.7, 0.6);   // Red base color
    vec3 color2 = hsl(pi, 0.7, 0.6);    // Cyan base color

    // Interpolate between colors based on radius and angle
    float mixRatio = r * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixRatio);

    // Add noise for dynamic effect
    float noiseValue = turbulent(uv * 5.0) * 0.2;
    finalColor += noiseValue;

    return vec4(finalColor, 1.0);
}
