vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Add time-based rotation
    theta += iTime * 0.5;

    // Create kaleidoscopic effect by mirroring angles
    theta = mod(theta, pi/2.0) * 2.0;
    theta = fract(theta / (pi*2.0)) * pi*2.0;

    // Generate pattern using trigonometric functions and noise
    float pattern = sin(theta * 4.0 + iTime) * cos(r * 10.0 - iTime);
    pattern += turbulent(vec3(theta, r, iTime * 0.5)) * 0.5;

    // Create vibrant color transitions using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.2 + theta / (pi*2.0)) * 360.0,
        0.7 + 0.3 * sin(iTime * 0.5 + r * 10.0),
        0.5 + 0.5 * pattern
    );

    // Convert to RGB and output
    vec3 color = hsl2rgb(hslColor);
    return vec4(color, 1.0);
}
