vec4 Effect2(vec2 uv) {
    // Animate based on time
    float t = iTime * 0.5;

    // Create rotating pattern with UV coordinates
    vec2 st = uv - 0.5;
    float angle = length(st) * 10.0 + t * 2.0;
    vec2 rotated = vec2(
        cos(angle) * st.x - sin(angle) * st.y,
        sin(angle) * st.x + cos(angle) * st.y
    );

    // Create color gradient with HSL
    vec3 color1 = hsl(length(rotated) * 0.5, 0.7, 0.6);
    vec3 color2 = hsl(length(rotated) * 0.5 + 0.5, 0.7, 0.6);

    // Mix colors based on sine wave pattern
    float mixAmount = sin(length(st) * 4.0 + t) * 0.5 + 0.5;
    vec3 mixedColor = mix(color1, color2, mixAmount);

    // Add undulating noise effect
    float noiseValue = noise(vec3(uv * 8.0 + t, t)) * 0.5;
    mixedColor += noiseValue;

    // Create darkening effect towards edges
    float edgeDarken = smoothstep(1.0, 0.0, length(uv - 0.5) * 2.0);
    mixedColor *= edgeDarken;

    return vec4(mixedColor, 1.0);
}
