vec4 Effect2(vec2 uv) {
    // Create a coordinate system centered at 0.5
    vec2 st = uv - 0.5;

    // Add rotation based on time
    float angle = sin(iTime * 0.5) + cos(iTime * 0.3);
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rot * st;

    // Create a scaling factor that changes over time
    float scale = 1.0 + 0.5 * sin(iTime * 0.7);
    rotated *= scale;

    // Add noise to create organic movement
    vec3 noisePos = vec3(rotated, iTime * 0.2);
    float n = noise(noisePos) * 0.5 + 0.5;

    // Create color based on position and time
    vec3 hslColor = vec3(
        sin(iTime * 0.1 + rotated.x * 2.0) * 0.5 + 0.5,
        0.7,
        0.8
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Create a pattern using multiple layers of lines
    float layer1 = sin(rotated.x * 4.0 + iTime) * 0.5;
    float layer2 = cos(rotated.y * 4.0 + iTime * 0.7) * 0.5;

    // Combine layers and noise for final pattern
    float pattern = (layer1 + layer2 + n) * 0.333;

    // Create the final color with alpha
    vec4 finalColor = vec4(rgbColor, 1.0);

    return finalColor;
}
