vec4 Effect2(vec2 uv) {
    // Create a grid of 30x30 squares
    float gridSize = 30.0;
    vec2 sqPos = floor(uv * gridSize) / gridSize;

    // Calculate rotation angle based on time and position
    float angle = iTime + sqPos.x * 10.0 + sqPos.y * 5.0;

    // Create a rotating square pattern using transformation matrix
    vec2 rotatedUV = uv * 2.0 - 1.0;
    rotatedUV = vec2(
        cos(angle) * rotatedUV.x - sin(angle) * rotatedUV.y,
        sin(angle) * rotatedUV.x + cos(angle) * rotatedUV.y
    );

    // Add noise and turbulence for dynamic effect
    float noiseValue = turbulent(rotatedUV * 5.0);
    noiseValue += noise(vec3(uv * 10.0, iTime)) * 0.5;

    // Create color shift effect using HSL colors
    vec3 color = hsv2rgb(vec3(
        (sqPos.x + sqPos.y + iTime) * 0.2,
        0.7 + noiseValue * 0.3,
        0.8 + noiseValue * 0.2
    ));

    // Add pulsing effect
    color *= 1.5 - abs(noiseValue);

    return vec4(color, 1.0);
}
