vec4 Effect2(vec2 uv) {
    float t = iTime * 0.5;

    // Create shifting pattern using sine and noise
    float pattern = sin(uv.x * 10.0 + t) * 0.5 + 0.5;
    pattern += noise(vec2(uv.x * 5.0, t)) * 0.3;
    pattern += turbulent(vec2(uv.x * 2.0, t)) * 0.2;

    // Create smooth color transitions
    vec3 color = hsl2rgb(vec3(
        (uv.y + sin(t) * 0.5 + 0.5) * 360.0,  // Hue
        0.7,                                    // Saturation
        0.5 + pattern * 0.5                     // Lightness
    ));

    return vec4(color, 1.0);
}
