vec4 Effect2(vec2 uv) {
    float time = iTime * 0.25;

    // Create a grid of rotating squares
    vec2 st = uv * 10.0 - 5.0;
    st = fract(abs(st)) - 0.5;

    // Rotate each square based on time
    float angle = sin(time) * 0.5 + 0.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rot * st;

    // Create square mask
    float square = max(abs(rotated.x), abs(rotated.y)) < 0.5 ? 1.0 : 0.0;

    // Add noise and color variations
    vec3 color = vec3(
        sin(time + uv.x * 2.0) * 0.5 + 0.5,
        sin(time + uv.y * 2.0) * 0.5 + 0.5,
        sin(time + (uv.x + uv.y) * 2.0) * 0.5 + 0.5
    );

    // Mix in some HSL colors for vibrancy
    vec3 hslColor = vec3(
        time * 10.0,
        0.7,
        0.6
    );
    color += hsl2rgb(hslColor) * 0.5;

    // Add turbulence effect
    float turb = turbulent(uv * 4.0 + time);
    color *= square * (1.0 + turb * 0.3);

    return vec4(color, 1.0);
}
