vec4 Effect2(vec2 uv) {
    // Center the UV coordinates
    vec2 st = uv - 0.5;

    // Create rotation matrix
    float angle = sin(iTime * 0.5 + length(st)) * 0.5 + iTime * 0.1;
    mat2 rot = mat2(cos(angle), -sin(angle),
                    sin(angle), cos(angle));

    // Apply rotation and create pulsating effect
    vec2 rotated = rot * st * (0.5 + 0.5 * sin(iTime * 2.0 + length(st) * 10.0));

    // Add turbulence to position
    vec2 pos = rotated + turbulent(rotated * 10.0 + iTime) * 0.1;

    // Create color transitions using HSL
    float hue = fract(iTime * 0.2 + length(pos) * 5.0);
    vec3 color1 = hsl(hue, 1.0, 0.5);
    vec3 color2 = hsl(hue + 0.5, 1.0, 0.5);

    // Combine colors based on position
    float blend = smoothstep(0.0, 1.0, sin(length(pos) * 5.0 + iTime * 2.0));
    vec3 finalColor = mix(color1, color2, blend);

    // Add noise to create dynamic patterns
    finalColor *= 1.0 + noise(vec3(pos * 10.0, iTime)) * 0.5;

    return vec4(finalColor, 1.0);
}
