vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for cellular noise pattern
    vec2 st = uv * 50.0;

    // Calculate cell centers using floor function
    vec2 cellCenter = (floor(st) + 0.5) / 50.0;

    // Compute distance from current position to cell center
    float dist = length(uv - cellCenter);

    // Add turbulence for more organic pattern
    float turb = turbulent(vec3(uv * 10.0, iTime));
    dist += turb * 0.2;

    // Create rotating color shifts using HSL
    vec3 hslColor = vec3(
        mod(dist * 5.0 + iTime * 2.0, 1.0),  // Hue with time offset
        0.7 + dist * 0.3,                     // Saturation
        0.5 + turb * 0.2                      // Lightness influenced by turbulence
    );

    // Convert HSL to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
