vec4 Effect2(vec2 uv) {
    // Offset coordinates based on time for movement
    vec2 st = uv + 0.5 * sin(iTime) * vec2(0.1);

    // Create shifting pattern using turbulence
    float t = turbulent(st * 10.0 + vec2(sin(iTime), cos(iTime)) * 5.0);

    // Convert to HSL color space for dynamic coloring
    vec3 hslColor = vec3(
        sin(iTime * 2.0) * 0.5 + 0.5,   // Hue variation over time
        1.0,                             // Full saturation
        abs(t * 0.5 + 0.5)               // Lightness based on turbulence
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
