vec4 Effect2(vec2 uv) {
    // Create a base pattern using time and position
    float d = sin(uv.x * 10.0 + iTime) * 0.5 + 0.5;

    // Add noise variation
    vec2 noisePos = uv * 5.0 + vec2(iTime);
    float n = noise(noisePos);

    // Create turbulence effect
    vec2 turbPos = uv * 10.0 + vec2(sin(iTime), cos(iTime));
    float t = turbulent(turbPos);

    // Combine effects and create color
    vec3 color = hsl(
        sin(uv.y * 5.0 + iTime) * 0.5 + 0.5,   // Hue variation
        0.7 + n * 0.3,                         // Saturation
        0.5 + t * 0.5                          // Lightness
    );

    // Create shifting lines effect
    float line = sin(d * 10.0 + iTime) * 0.5 + 0.5;
    color *= line;

    return vec4(color, 1.0);
}
