vec4 Effect2(vec2 uv) {
    // Create flowing pattern using sine waves and noise
    float time = iTime * 0.5;

    // Combine position and time for dynamic patterns
    vec2 st = uv * 10.0 + vec2(sin(time), cos(time));

    // Add turbulence to create complex flow
    float turb = turbulent(st);

    // Create color variations using HSL
    vec3 color = hsl(
        sin(uv.x * 5.0 + time) * 0.5 + 0.5,   // Hue variation
        0.7,                                    // Saturation
        0.5 + turb * 0.2                       // Lightness with turbulence
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
