vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 5.0 + vec2(sin(iTime)*3.0, cos(iTime*0.7)*2.0);

    // Generate noise pattern with time variation
    float turbulentValue = turbulent(st + iTime);

    // Create color from noise values
    vec3 color = hsl2rgb(vec3(
        (turbulentValue + iTime) * 0.5,   // Hue
        0.7 + turbulentValue*0.3,         // Saturation
        0.5 + turbulent(st*0.5)*0.5       // Lightness with additional turbulence
    ));

    return vec4(color, 1.0);
}
