vec4 Effect2(vec2 uv) {
    // Create polar coordinates from UV
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Generate noise values with time offset
    float n1 = noise(vec3(r * 10.0, theta * 10.0, iTime));
    float n2 = noise(vec3(uv * 10.0 + iTime, 0.0));

    // Create color from HSL values
    vec3 color = hsl2rgb(vec3(
        (theta / PI) * 0.5 + 0.5,
        abs(n1) * 0.7 + 0.3,
        abs(n2) * 0.6 + 0.4
    ));

    // Add turbulence effect
    float turb = turbulent(vec3(uv * 10.0, iTime));
    color = mix(color, vec3(0.5), abs(turb) * 0.3);

    return vec4(color, 1.0);
}
