vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 st = uv - 0.5;

    // Create polar coordinates
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Add time-based rotation
    angle += iTime * 0.1;

    // Create sine wave patterns with different frequencies and phases
    float r = sin(radius * 8.0 + angle) * 0.5 + 0.5;
    float g = sin(radius * 8.0 + angle + 2.094) * 0.5 + 0.5; // 120 degrees offset
    float b = sin(radius * 8.0 + angle - 2.094) * 0.5 + 0.5; // 120 degrees offset

    // Add some noise for extra complexity
    vec3 noise = vec3(turbulent(st * 4.0));

    // Combine colors and noise
    vec3 color = vec3(r, g, b) * (0.7 + noise);

    return vec4(color, 1.0);
}
