vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for rotation effect
    float angle = atan2(uv.y, uv.x) + iTime * 2.0;
    float radius = length(uv);

    // Create multiple rotating rings with pulsating effects
    radius += sin(radius * 10.0 + iTime) * 0.2;
    radius += sin(mod(angle, pi / 4.0)) * 0.3;
    radius += sin(mod(angle * 8.0, pi)) * 0.15;
    // Create pulsating color effect
    vec3 color = hsl2rgb(vec3(
        fract(radius * 0.1 + iTime) * pi,
        0.8,   // Saturation
        0.5    // Lightness
    ));
    return vec4(color * smoothstep(0.0, 1.0, 1.0 - length(uv)), 1.0);
}
