vec4 Effect2(vec2 uv) {
    // Create rotating pattern using polar coordinates
    vec2 center = uv - 0.5;
    float angle = atan(center.y, center.x);
    float dist = length(center);

    // Add time-based rotation
    angle += iTime * 0.5;

    // Create kaleidoscopic effect by mirroring
    vec2 mirroredUV = fract(abs(sin(angle) * uv + cos(angle)));

    // Generate triangle pattern using distance field
    float triangle = abs(mirroredUV.x - mirroredUV.y);
    triangle = smoothstep(0.3, 0.7, triangle);

    // Add turbulence for dynamic effect
    vec2 noiseUV = mirroredUV * 10.0;
    float turbulence = turbulent(noiseUV + iTime);

    // Create vibrant colors using HSL
    vec3 color = hsl(
        fract(iTime * 0.2) * 360.0,   // Hue variation over time
        0.7 + triangle * 0.3,         // Saturation
        0.5 + turbulence * 0.5        // Lightness modulation
    );

    // Combine effects and output
    return vec4(color * (1.0 - triangle), 1.0);
}
