vec4 Effect2(vec2 uv) {
    // Create rotating pattern using sine and cosine
    float time = iTime * 0.5;
    vec2 st = uv * 2.0 - 1.0;

    // Add noise and turbulence for sparkles
    float n = noise(st + vec2(sin(time), cos(time))) * 0.5 + 0.5;
    float t = turbulent(st * 4.0) * 0.5 + 0.5;

    // Create color from HSL values
    vec3 color = hsl2rgb(vec3(
        (st.x + st.y + time) * 0.5 + 0.5,
        0.7,
        n * t * 0.8 + 0.2
    ));

    // Add lighting effect
    float dist = length(st);
    color *= smoothstep(1.0, 0.0, dist) * 2.0;

    return vec4(color, 1.0);
}
