vec4 Effect2(vec2 uv) {
    // Create rotating coordinate system
    float t = iTime * 0.5;
    vec2 st = uv * 10.0;

    // Add rotation based on time
    st = mat2(cos(t), sin(t), -sin(t), cos(t)) * (st - 5.0) + 5.0;

    // Create flowing pattern using noise and turbulence
    float noiseVal = turbulent(st);
    vec3 color = hsl2rgb(vec3(
        mod(length(st) * 0.1 + t, 1.0),  // Hue variation
        0.7,                              // Saturation
        0.5 + noiseVal * 0.5              // Brightness modulation
    ));

    return vec4(color, 1.0);
}
