vec4 Effect2(vec2 uv) {
    // Translate UV to center coordinates
    vec2 st = uv - 0.5;

    // Add rotation based on time
    float angle = atan2(st.y, st.x) + 0.5 * iTime;

    // Calculate radius with expansion over time
    float r = length(st);
    r += sin(iTime) * 0.5;

    // Create concentric circles pattern
    float circleIndex = floor(r * 10.0); // Number of circles
    float smoothTransition = fract(circleIndex / 10.0 + 0.5 * sin(iTime));

    // Generate color transitions using HSL
    vec3 hslColor;
    hslColor.x = (circleIndex / 10.0 + 0.5 * sin(iTime)) * PI; // Hue variation
    hslColor.y = 0.7; // Saturation
    hslColor.z = 0.5; // Lightness

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
