vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create wave patterns using sine and cosine functions
    vec2 st = uv * 8.0; // Scale coordinates for more detailed pattern
    float waveX = sin(st.x + time) * 0.5 + 0.5;
    float waveY = cos(st.y - time) * 0.5 + 0.5;

    // Combine waves and add pulsation effect
    float intensity = (waveX + waveY) / 2.0;
    intensity = mod(intensity + sin(time * 3.0), 1.0);

    // Create color transition using HSL to RGB conversion
    vec3 colorA = hsl2rgb(vec3(0.0, 1.0, 0.5)); // Green base color
    vec3 colorB = hsl2rgb(vec3(0.7, 1.0, 0.5)); // Purple transition color

    // Mix colors based on intensity and add pulsation
    vec3 finalColor = mix(colorA, colorB, intensity);

    return vec4(finalColor, 1.0);
}
