vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to a polar system with rotation over time
    vec2 center = vec2(0.5, 0.5);
    vec2 st = uv - center;
    float angle = iTime * 0.1;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedSt = rotMatrix * st;
    // Create a grid pattern with cells
    vec2 gridPos = floor(rotatedSt * 10.0);
    vec2 cellCenter = (gridPos + 0.5) / 10.0;

    // Calculate turbulence for added complexity
    float t2d = turbulent(cellCenter + iTime * 0.1);
    float t3d = turbulent(vec3(cellCenter, iTime * 0.1));

    // Create pulsing color effect using HSV
    vec3 hsvColor = vec3(180.0, 0.7, 0.5); // Base cyan color
    hsvColor.z *= sin(iTime + gridPos.x * 2.0) * 0.5 + 0.5; // Pulsing intensity

    // Combine turbulence with color parameters
    hsvColor.y += t3d * 0.3;
    hsvColor.z += (t2d + t3d) * 0.2;

    // Convert to RGB and output
    return vec4(hsv2rgb(hsvColor), 1.0);
}
