vec4 Effect2(vec2 uv) {
    // Base frequency modulation
    float freq = sin(iTime * 0.5 + 3.1415926) * 50.0;

    // Dynamic color components using sine waves and noise
    vec3 hslColor = vec3(
        sin(freq * uv.x + iTime) * 0.5 + 0.5,
        noise(vec2(uv.y * 10.0, iTime)) * 0.5 + 0.5,
        sin(freq * uv.y - iTime) * 0.5 + 0.5
    );

    // Convert HSL to RGB and add turbulence effect
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor += vec3(
        turbulent(vec2(uv.x * 10.0, uv.y * 10.0) + iTime),
        turbulent(vec2(uv.x * 5.0, uv.y * 5.0) + iTime * 0.5),
        turbulent(vec2(uv.x * 2.0, uv.y * 2.0) + iTime * 0.75)
    ) * 0.3;

    // Add smooth sine wave transitions
    float transition = sin(iTime * 0.2 + uv.x * 10.0) * 0.5 + 0.5;
    rgbColor = mix(rgbColor, vec3(0.2), transition);

    return vec4(rgbColor, 1.0);
}
