vec4 Effect2(vec2 uv) {
    // Create a base color using UV coordinates and time
    vec3 baseColor = hsl(uv.x * 0.5 + 0.5, uv.y * 0.5 + 0.5, 0.5);

    // Add flowing movement using noise and turbulence
    float flow1 = turbulent(uv * 10.0) * 0.5;
    float flow2 = turbulent(vec3(uv * 5.0, iTime)) * 0.3;

    // Combine flows and create a shifting color pattern
    vec3 color = hsl(
        uv.x + iTime * 0.1 + flow1 + flow2,
        0.7 + noise(uv * 8.0) * 0.3,
        0.5 + turbulent(uv * 4.0) * 0.2
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
