vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv * 10.0; // Scale up for grid effect
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create smooth color transitions using HSL
    vec3 hslColor;
    hslColor.x = (theta * 0.15 + iTime * 0.2) + 0.5; // Hue variation with time
    hslColor.y = 0.7 + turbulent(vec2(r, theta)) * 0.3; // Saturation modulation
    hslColor.z = 0.5 + sin(r * 0.1 - iTime) * 0.5; // Lightness variation

    // Add turbulence for dynamic noise effect
    float turb = turbulent(vec2(r, theta));
    vec3 color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
