vec4 Effect2(vec2 uv) {
    // Rotate coordinates over time
    float angle = iTime * 0.5;
    vec2 rotatedUv = vec2(
        uv.x * cos(angle) - uv.y * sin(angle),
        uv.x * sin(angle) + uv.y * cos(angle)
    );
    // Create grid pattern with triangles
    vec2 gridPos = fract(rotatedUv * 10.0);
    float triangle = step(0.5, gridPos.x + gridPos.y);
    // Calculate color based on time and position
    vec3 hslColor = vec3(
        (iTime + gridPos.x * 2.0) * 0.1,
        0.7,
        0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);
    // Add glowing edges using distance field
    float edgeDistance = min(gridPos.x, gridPos.y);
    edgeDistance = smoothstep(0.4, 0.6, edgeDistance);

    // Combine color and glow effect
    vec3 finalColor = rgbColor * (1.0 + edgeDistance * 0.5);
    return vec4(finalColor, 1.0);
}
