vec4 Effect2(vec2 uv) {
    // Base color using HSL for easy hue manipulation
    vec3 baseColor = hsl2rgb(vec3(0.5 + 0.5 * sin(iTime * 0.5), 1.0, 0.6));

    // Add turbulence to create flickering effect
    float t = turbulent(uv * 0.5) * 0.5;
    t += turbulent(uv * 0.25 + vec2(sin(iTime)) * 0.5) * 0.3;

    // Create sine wave modulation for flicker
    float flicker = sin(iTime * 4.0 + uv.x * 10.0);
    flicker = (flicker + 1.0) * 0.5; // Scale to 0-1

    // Combine colors and add noise
    vec3 finalColor = baseColor * (t * 2.0 + 0.5);
    finalColor += noise(vec3(uv, iTime)) * 0.3;

    // Add flicker effect
    finalColor *= mix(1.0, 1.5, flicker);

    return vec4(finalColor, 1.0);
}
