vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 pos = uv * 10.0 + iTime;

    // Create a flowing wave pattern using turbulence
    float wave1 = turbulent(pos);

    // Add another layer of waves with different scale and movement
    vec2 pos2 = uv * 5.0 + vec2(iTime, iTime * 0.7);
    float wave2 = turbulent(pos2);

    // Combine waves for more complex pattern
    float finalWave = (wave1 + wave2) * 0.5;

    // Create color transitions using HSL
    vec3 color1 = hsl(iTime * 0.1 + uv.x * 2.0, 0.7, 0.6);
    vec3 color2 = hsl(iTime * 0.15 + uv.y * 2.0, 0.8, 0.5);

    // Mix colors based on wave pattern
    vec3 finalColor = mix(color1, color2, sin(finalWave * pi + iTime));

    return vec4(finalColor, 1.0);
}
